    private void TotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TotalActionPerformed
          //Variablres Gastos Administrativos
        float SalarioM,ImpuestoM,Prestamos,Otros1,Total;
        //Gastos de Mercadeo
        float Tarjetitas,Otros2;
        //Gastos de Mantenimiento
        float Pintura,Inmuebles,EquipoComputo,Otros3;
       //Gastos Fijos
        float Agua,Gas,Luz,Renta,Telefono,Otros4;
        //Gastos Variables
        float TrasportePrivado,TrasportePublico,Otros5;
        //Gastos Mercancia
        float Alimentos,Bebidas,Desechables,Otros6;

        

        
        
        //Parsear Gastos Administrativos
        SalarioM=Float.parseFloat(CajaSm.getText());
        ImpuestoM=Float.parseFloat(CajaIm.getText());
        Prestamos=Float.parseFloat(CajaPrestamos.getText());
        Otros1=Float.parseFloat(Caja1.getText());
        //Parsear Gastos de Mercadeo
         Tarjetitas=Float.parseFloat(CajaTarjeti.getText());
         Otros2=Float.parseFloat(Caja2.getText());
         //Parsear Gastos de Manteniminto
         Pintura=Float.parseFloat(CajaPintura.getText());
         Inmuebles=Float.parseFloat(CajaInmueble.getText()); 
         EquipoComputo=Float.parseFloat(CajaEquipo.getText());
         Otros3=Float.parseFloat(Caja3.getText());
        //Parsear Gastos Fijos
         Agua=Float.parseFloat(CajaAgua.getText());
         Gas=Float.parseFloat(CajaGas.getText());
         Luz=Float.parseFloat(CajaLuz.getText());
         Renta=Float.parseFloat(CajaRenta.getText());
         Telefono=Float.parseFloat(CajaTelefono.getText());
         Otros4=Float.parseFloat(Caja4.getText());
         //Parsear Gastos Variables
         TrasportePrivado=Float.parseFloat(CajaTrasportePrivado.getText());
         TrasportePublico=Float.parseFloat(CajaTrasportePublico.getText());
         Otros5=Float.parseFloat(Caja5.getText());
         //Parsear Gastos Mercancia
         Alimentos=Float.parseFloat(CajaAlimentos.getText());
         Bebidas=Float.parseFloat(CajaBebidas.getText());
         Desechables=Float.parseFloat(CajaDesechable.getText());
         Otros6=Float.parseFloat(Caja6.getText());

         
         
        //Operaciones
        Total=SalarioM+ImpuestoM+Prestamos+Otros1+Tarjetitas+Otros2+Pintura+Inmuebles+EquipoComputo+Otros3+Agua+Gas+Luz+Renta+Telefono+Otros4+TrasportePrivado+TrasportePublico+Otros5+Alimentos+Bebidas+Desechables+Otros6;
         
                
    //Mostrara en panatalla el total
    CajaTotal.setText(String.valueOf(Total));
        
    }//GEN-LAST:event_TotalActionPerformed

